# bible books descriptions from: https://city.org.nz/kit/bible-overview

Book.create(name: "Genesis", testament: "Old Testament", author: "Moses", group: "Pentateuch", description: "Genesis is  the first book of the Bible. Its name derives from the opening words: “In the beginning….” Genesis narrates the primeval history of the world and the patriarchal history of the Israelite people.", image_url: "http://www.ingodsimage.com/wp-content/uploads/2017/01/Genesis-The-Book-of-Beginnings.jpg")
Book.create(name: "Exodus", testament: "Old Testament", author: "Moses", group: "Pentateuch", description: "God appoints Moses to lead the Israelites out of slavery in Egypt to the Promised Land of Canaan, establishing a special relationship with them on the way to Mount Sinai.", image_url: "https://qph.fs.quoracdn.net/main-qimg-c8fcc9a91d8338ceba3b51a7395d1bd4")
Book.create(name: "Leviticus", testament: "Old Testament", author: "Moses", group: "Pentateuch", description: "God gives Israel rules to live by and instructions to present themselves holy before Him.", image_url: "https://youthvids.com/wp-content/uploads/2018/02/the-book-of-leviticus-overview.jpg")
Book.create(name: "Numbers", testament: "Old Testament", author: "Moses", group: "Pentateuch", description: "A sequel to Exodus, Numbers takes its name from two censuses (or numberings) of the people of Israel, following their journey through the wilderness for forty years.", image_url: "https://www.evidenceunseen.com/wp-content/uploads/2019/03/numbers.jpg")
Book.create(name: "Deutoronomy", testament: "Old Testament", author: "Moses", group: "Pentateuch", description: "A farewell speech from Moses to the people of Israel shortly before his death, Deuteronomy recaps the promises of God and provides instructions to obey Him in the Promised Land.", image_url: "https://www.gotquestions.org/img/OG/Book-of-Deuteronomy.jpg")
Book.create(name: "Joshua", testament: "Old Testament", author: "Unknown", group: "Historical Books", description: "A book of conquest, Joshua details the Israelites' invasion and eventual occupation of the Promised Land through faith and action.", image_url: "https://overviewbible.com/wp-content/uploads/2013/07/Joshua-jericho-ai-achan-caleb-judah-12-tribes-old-testament-overviewbible.com-online-Bible-guide-1024x658.jpg")
Book.create(name: "Judges", testament: "Old Testament", author: "Unknown", group: "Historical Books", description: "Israel enters a cycle of sin, suffering defeat and oppression, only to cry out to God for deliverance, who sends leaders (called judges) to help them.", image_url: "https://i0.wp.com/www.anchoredresources.org/wp/wp-content/uploads/2018/03/Judges.jpg")
Book.create(name: "Ruth", testament: "Old Testament", author: "Unknown", group: "Historical Books", description: "Occurring during some of the darkest days in Israel's history, Ruth follows the journey of two widows who lose everything, but find hope through God.", image_url: "https://i.pinimg.com/originals/85/7f/73/857f73b7ef2020d66cfa6e9a45f230c0.jpg")
Book.create(name: "1 Samuel", testament: "Old Testament", author: "Unknown", group: "Historical Books", description: "Israel rejects God's chosen leader, Samuel (a judge), and demands a king despite God's warnings.", image_url: "https://godinourliveseveryday.com/wp-content/uploads/2019/12/Book-Of-1-Samuel-images.jpg")
Book.create(name: "2 Samuel", testament: "Old Testament", author: "Unknown", group: "Historical Books", description: "The life and career of King David, who subdues Israel's enemies and doubles the size of the kingdom, but is not without failings.", image_url: "https://d2ecbaqsz6tho7.cloudfront.net/main/poster/_1920x1080_crop_center-center_85_none/2nd-samuel-artwork_video-582936bae9bdc.png")
Book.create(name: "1 Kings", testament: "Old Testament", author: "Unknown", group: "Historical Books", description: "Israel enjoys a period of peace and prosperity under King Solomon, but later splits in two after Rehoboam (his son) takes the throne.", image_url: "http://metroimages.s3.amazonaws.com/albumart/1kings-web720.png")
Book.create(name: "2 Kings", testament: "Old Testament", author: "Unknown", group: "Historical Books", description: "The kings of Israel and Judah ignore God and His prophets, eventually falling captive to invading nations and are exiled to foreign lands.", image_url: "https://biblestudyministry.com/wp-content/uploads/2019/01/2kings-series.jpg")
Book.create(name: "1 Chronicles", testament: "Old Testament", author: "Ezra", group: "Historical Books", description: "Written to encourage the people returning from Babylonian exile, 1 Chronicles recaps the history and genealogy of Israel, emphasising the spiritual significance of David and future Messianic King.", image_url: "https://www.gotquestions.org/img/OG/Book-of-1-Chronicles.jpg")
Book.create(name: "2 Chronicles", testament: "Old Testament", author: "Ezra", group: "Historical Books", description: "A continuation of the previous book, 2 Chronicles focuses on the kings of Israel, from King Solomon and the building of the temple, to subsequent division, exile and return from captivity.", image_url: "https://www.gotquestions.org/img/OG/Book-of-2-Chronicles.jpg")
Book.create(name: "Ezra", testament: "Old Testament", author: "Ezra", group: "Historical Books", description: "Fulfilling the promises of God, the Israelites return from exile after seventy years and rebuild the temple.", image_url: "https://alchetron.com/cdn/book-of-ezra-6239c114-cf46-4c9b-9dd5-cbd3dbcbd3b-resize-750.png")
Book.create(name: "Nehemiah", testament: "Old Testament", author: "Nehemiah", group: "Historical Books", description: "Despite local opposition, Nehemiah returns to Jerusalem from exile, rallying the people to rebuild the city walls and gates in just fifty-two days.", image_url: "https://georgehawke.files.wordpress.com/2013/03/jerusalem-wall-nehemiah.jpg")
Book.create(name: "Esther", testament: "Old Testament", author: "Unknown", group: "Historical Books", description: "Occurring during the exile of Israel, Esther is a Jewish queen to a Persian king, who intercedes on behalf of her people to save them from a genocidal plot.", image_url: "https://ministrypass-prod.s3.amazonaws.com/uploads/2019/09/God-Remembers-The-Book-Of-Esther-Sermon-Seriesjpg.jpg")
Book.create(name: "Job", testament: "Old Testament", author: "Unknown", group: "Poetry and Wisdom Books", description: "A righteous man named Job loses everything and suffers greatly, but remains faithful to God and is blessed abundantly.", image_url: "https://storage.googleapis.com/night-fox-clients-storage/fbchurch.org/resources/photos/primary/1000.59c963ad68ee22.65740761.jpg")
Book.create(name: "Psalms", testament: "Old Testament", author: "Various", group: "Poetry and Wisdom Books", description: "A collection of 150 songs of worship and praise to God that includes prophecies of the coming Messiah.", image_url: "https://lh3.googleusercontent.com/proxy/W4bxRNtXFMlpT4-Z_3P0ae0L-_Xuv5sD_fCq4csO3srYymEOLRRVG9HZJJwwUheMQPPIJcPSLuk3zImpoxM7ZaMqbMynHT_B90qtmf4agPVyVzdn192PweoiK2QRVf8Hf-qSmtOkqMPWA1do_a0")
Book.create(name: "Proverbs", testament: "Old Testament", author: "Solomon", group: "Poetry and Wisdom Books", description: "The book of Proverbs contains God's divine wisdom, covering a variety of topics for every area of life.", image_url: "https://images.squarespace-cdn.com/content/v1/57c5c910c534a57ea234deef/1560891945352-M8D05DTAXN42XELZ5E3G/01+Proverbs+ProPresenter.jpg")
Book.create(name: "Ecclesiastes", testament: "Old Testament", author: "Solomon", group: "Poetry and Wisdom Books", description: "Solomon's analysis of life, which is meaningless and empty without God.", image_url: "https://bcnewton.files.wordpress.com/2018/02/ecc-bg.jpg")
Book.create(name: "Song of Songs", testament: "Old Testament", author: "Solomon", group: "Poetry and Wisdom Books", description: "A passionate yet gentle song of love between a husband and wife, symbolising God's relationship with us.", image_url: "https://tptbibleschool.com/wp-content/uploads/2017/06/Event_Images_SOS.jpg")  
Book.create(name: "Isaiah", testament: "Old Testament", author: "Isaiah", group: "Major Prophets", description: "The first book of the Major Prophets, Isaiah contains warnings of God's coming judgement and detailed prophecies about the Messiah.", image_url: "https://images.squarespace-cdn.com/content/v1/58a72d2215d5db819b7b4f35/1517766621684-11YLBEG1VRIBYF372POY/logo.jpg?format=1000w")
Book.create(name: "Jeremiah", testament: "Old Testament", author: "Jeremiah", group: "Major Prophets", description: "Known as the weeping prophet, Jeremiah passionately pleads with the people to repent before the coming Babylonian captivity, but is ignored.", image_url: "https://i.gr-assets.com/images/S/compressed.photo.goodreads.com/books/1566840136l/52667503._SX0_SY0_.jpg")
Book.create(name: "Lamentations", testament: "Old Testament", author: "Jeremiah", group: "Major Prophets", description: "Lamentations is a book of sadness that reflects on the destruction of Jerusalem and captivity of Israel.", image_url: "https://images.squarespace-cdn.com/content/v1/5492da8fe4b070adab956f3b/1557181805087-UEMRO7JNDQNUC4RYHLOB/Lamentations+series+.jpg?format=1500w")
Book.create(name: "Ezekiel", testament: "Old Testament", author: "Ezekiel", group: "Major Prophets", description: "Ezekiel is called by God to preach a message of judgement and deliverance for the captives living in Babylon.", image_url: "https://storage.googleapis.com/night-fox-clients-storage/fbchurch.org/resources/photos/primary/500.59cebf5b996637.14587256.jpg")
Book.create(name: "Daniel", testament: "Old Testament", author: "Daniel", group: "Major Prophets", description: "Like Ezekiel, Daniel has been taken to Babylon in captivity and receives prophetic visions while serving in the courts of the king.", image_url: "https://www.csmedia1.com/graceanglican.church/danielsermonserieslight.jpg")
Book.create(name: "Hosea", testament: "Old Testament", author: "Hosea", group: "Minor Prophets", description: "The first book of the Minor Prophets, Hosea is a tragic love story that demonstrates God's unending love for His people despite their unfaithfulness.", image_url: "https://faithequip.org/wp-content/uploads/2017/03/hosea-sermonseriesgraphic.jpg")
Book.create(name: "Joel", testament: "Old Testament", author: "Joel", group: "Minor Prophets", description: "Joel warns the people to repent and turn back to God before judgement falls upon them.", image_url: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSj35Bya6w1X40sw-raBHhxXxAmzhtWSjDIx0bEuTy3y-hlLz1-Gb9BBm_92dtfzyFZdcE&usqp=CAU")
Book.create(name: "Amos", testament: "Old Testament", author: "Amos", group: "Minor Prophets", description: "A shepherd named Amos prophesies to the northern kingdom which has become self-sufficient and indifferent towards God during a time of great prosperity.", image_url: "https://i.ytimg.com/vi/mglSEYsPHTs/hqdefault.jpg")
Book.create(name: "Obadaiah", testament: "Old Testament", author: "Obadaiah", group: "Minor Prophets", description: "Only one chapter, Obadiah demonstrates God's ongoing protection of His people and coming judgement on the nation of Edom, which was indifferent during the Babylonian plunder of Jerusalem.", image_url: "https://3.bp.blogspot.com/-dokek5sZBa0/Wj11RJub2WI/AAAAAAAAIlQ/ZQ1d7bGfZ6M3WajJr6WebkVgcwxQjIKfwCLcBGAs/s1600/Obadiah%252C%2Bbook%2Bof--pride%2Bwill%2Bbring%2Byou%2Bdown.jpg")
Book.create(name: "Jonah", testament: "Old Testament", author: "Jonah", group: "Minor Prophets", description: "A reluctant prophet, Jonah is sent by God to Nineveh, but refuses and learns the futility of it in the belly of a giant fish.", image_url: "https://redeeminggod.com/wp-content/uploads/2017/12/jonah-conclusion.jpg")
Book.create(name: "Micah", testament: "Old Testament", author: "Micah", group: "Minor Prophets", description: "Micah warns of the coming judgement that will eventually exile the nation, and includes some of the clearest predictions of the Messiah.", image_url: "https://lh3.googleusercontent.com/proxy/N0Bir_0-YrTE1w5OGP5jxDM7_Mlk2MdpQcJwwq93Z5twoIbbVDZXi6GJ_K1dizVTdK7hFNK1NEOfTzQbcmBJ9vDmXeYHP8uCkAX0oIaHe5FjPsyJlw")
Book.create(name: "Nahum", testament: "Old Testament", author: "Nahum", group: "Minor Prophets", description: "Nahum is the second prophet sent to Nineveh (Jonah being the first) to preach God's judgement on the Assyrian city and empire.", image_url: "http://www.faiththeevidence.com/uploads/8/5/6/6/8566914/published/hqdefault_9.jpg?1527293719")
Book.create(name: "Habakkuk", testament: "Old Testament", author: "Habakkuk", group: "Minor Prophets", description: "God answers Habakkuk's complaints of wickedness and injustice in the land.", image_url: "https://media.proprofs.com/images/QM/user_images/1754155/1482399723.png")
Book.create(name: "Zephaniah", testament: "Old Testament", author: "Zephaniah", group: "Minor Prophets", description: "Written shortly before the fall of Judah (Southern Kingdom of Israel) to Babylonian conquest, Zephaniah warns the people and the surrounding nations that the day of the Lord is near.", image_url: "https://i.ytimg.com/vi/LkB81qC1QO4/hqdefault.jpg")
Book.create(name: "Haggai", testament: "Old Testament", author: "Haggai", group: "Minor Prophets", description: "Written after the Babylonian exile, work to rebuild the temple in Jerusalem had halted due to opposition and spiritual apathy, so Haggai motivates the people to finish.", image_url: "https://i.ytimg.com/vi/LkB81qC1QO4/hqdefault.jpg")
Book.create(name: "Zechariah", testament: "Old Testament", author: "Zechariah", group: "Minor Prophets", description: "Zechariah ministered with Haggai after the 70-year exile, encouraging the remnant to return to God.", image_url: "https://i0.wp.com/s3.amazonaws.com/churchplantmedia-cms/christchurchpca_tx/cck-sermonimage-zechariah.jpg")
Book.create(name: "Malachi", testament: "Old Testament", author: "Malachi", group: "Minor Prophets", description: "The last book of the Old Testament, Malachi is a beautiful expression of God's love for a nation that continues to disobey Him.", image_url: "https://i.pinimg.com/originals/47/b5/4d/47b54d44c503fd16a5cefc3fb22067d7.png")
Book.create(name: "Matthew", testament: "New Testament", author: "Matthew", group: "Gospels", description: "The first book of the New Testament, the Gospel of Matthew was primarily written for the Jews and references many Old Testament prophecies that were fulfilled by Jesus.", image_url: "http://images.sharefaith.com/images/3/1245382174282_84/img_mouseover3.jpg")
Book.create(name: "Mark", testament: "New Testament", author: "John Mark", group: "Gospels", description: "Mark is the shortest Gospel, which emphasises Jesus' servanthood and miracles.", image_url: "https://images.sharefaith.com/images/3/1245383229215_85/slide-02.jpg")
Book.create(name: "Luke", testament: "New Testament", author: "Luke", group: "Gospels", description: "Unlike the other Gospel writers, Luke was a Gentile who wrote an account of Jesus' life for those outside the Jewish faith.", image_url: "https://images.sharefaith.com/images/3/1245384286780_86/img_mouseover3.jpg")
Book.create(name: "John", testament: "New Testament", author: "John", group: "Gospels", description: "The last of the four Gospels, John is an eyewitness account of Jesus' ministry that focuses on the deeper meaning of events surrounding Christ's life, death and resurrection.", image_url: "https://m.media-amazon.com/images/M/MV5BNzU3ZjA0NTAtZDE3Mi00MWM5LTkxMDgtMjA5ZjlkNThjZGYzXkEyXkFqcGdeQXVyNDk4NDY3MTU@._V1_.jpg")
Book.create(name: "Acts", testament: "New Testament", author: "Luke", group: "Historical Narrative", description: "A historical narrative of the early church which was empowered by the Holy Spirit to spread the Good News.", image_url: "https://images.squarespace-cdn.com/content/v1/58b7381929687f370cca699f/1610478690174-1LWISFW07OS4FNS50JJ9/Acts.jpg?format=1500w")
Book.create(name: "Romans", testament: "New Testament", author: "Paul", group: "Paul's Letters", description: "An epistle to the believers in Rome (hence the name) where Paul plans to visit, Romans sets a theological foundation for faith through Jesus.", image_url: "https://www.papermasters.com/images/book-bible-romans.jpg")
Book.create(name: "1 Corinthians", testament: "New Testament", author: "Paul", group: "Paul's Letters", description: "The first of two letters from Paul to the believers in Corinth, 1 Corinthians was written in response to divisions and problems facing the local church.", image_url: "https://www.gotquestions.org/img/OG/Book-of-1-Corinthians.jpg")
Book.create(name: "2 Corinthians", testament: "New Testament", author: "Paul", group: "Paul's Letters", description: "The second and final letter from Paul to the church in Corinth, 2 Corinthians deals with persisting problems facing the believers there and warns against false teachers.", image_url: "https://i.ytimg.com/vi/MrYu_dxSowY/maxresdefault.jpg")
Book.create(name: "Galatians", testament: "New Testament", author: "Paul", group: "Paul's Letters", description: "A letter from Paul to the church in Galatia, the book is a foundational study that addresses the problem of Jewish legalism and the fullness of salvation found in Jesus.", image_url: "https://www.csmedia1.com/scottsdalechurch.org/galatians.jpg")
Book.create(name: "Ephesians", testament: "New Testament", author: "Paul", group: "Paul's Letters", description: "Written to the church in Ephesus during Paul's first imprisonment, Ephesians covers a variety of subjects including the gift of grace, love and how to walk as fruitful followers of Jesus.", image_url: "https://teleiosresearch.com/wp-content/uploads/2019/11/ephesians3.jpg")
Book.create(name: "Philippians", testament: "New Testament", author: "Paul", group: "Paul's Letters", description: "An encouraging letter from Paul to the church in Philippi explaining the attitude and outlook believers must have to experience the joy of the Lord.", image_url: "https://christandchristianity.files.wordpress.com/2015/11/url.jpeg")
Book.create(name: "Colossians", testament: "New Testament", author: "Paul", group: "Paul's Letters", description: "In this letter, Paul refutes certain false teachings that are impeding the church in Colossae, reaffirming the deity and superiority of Jesus Christ.", image_url: "https://images.squarespace-cdn.com/content/v1/551db60fe4b095e92c16c46b/1532619663656-ZFAJBBXLPKWEYS2RTKAH/Colossians---Screen-2.jpg")
Book.create(name: "1 Thessalonians", testament: "New Testament", author: "Paul", group: "Paul's Letters", description: "The first of two letters to the believers in Thessalonica, Paul writes to encourage and strengthen the church, emphasising the principles of holy living through faith, hope and love.", image_url: "https://images.squarespace-cdn.com/content/v1/58d1ce95ebbd1acdfecf2f9e/1539232537764-GMZN187FVAEUOXM6JE45/Slide.jpg?format=1500w")
Book.create(name: "2 Thessalonians", testament: "New Testament", author: "Paul", group: "Paul's Letters", description: "A follow-up letter of encouragement to the persecuted church of Thessalonica, Paul reaffirms Jesus' second coming and matters preceding that event.", image_url: "https://www.restonbible.org/wp-content/uploads/2021/05/sermon_2thess.jpg")
Book.create(name: "1 Timothy", testament: "New Testament", author: "Paul", group: "Paul's Letters", description: "A letter from Paul to a young pastor named Timothy, offering guidance and important principles for church leadership that still apply today.", image_url: "https://d113wk4ga3f0l0.cloudfront.net/c?o=eJyVkDFvwjAQhf_LSd3ATkiDSNaq7dABiqqq7WJZyZE4iWPjc0gB8d9rJFTo2OGG995J9707ApnBFSgG10EOtfeWcs4pYVLLg-nlSKwwmodhnapqT94ULVNaVkhssJ2RJTHrTDkUXpmeayyVFMqjFtbhTuFIt948mydZwuN0MUvjeXQfTb-fn2yzXHar8at8ed2ui_324_NdNXy1fpzG0zelja_3Ioola2wFE2hxH0g36WaTZEGOqvQ15IssmkCNZ8SLCMjWULgK-RH-V08Sof9Ta5QenZauJX7dE78us311ZYE0vYMrzUWWiky3Q2Ex_LsP_iydgENSBxSVkzvlz70eQoQOTqcfgtuOqw==&s=48d9258114f19138451026193ca1acdae4546b28")
Book.create(name: "2 Timothy", testament: "New Testament", author: "Paul", group: "Paul's Letters", description: "The second of two letters to Timothy, the book is probably Paul's final chronological epistle, urging his protégé to remain strong and faithful to Jesus.", image_url: "https://images.saymedia-content.com/.image/ar_16:9%2Cc_fill%2Ccs_srgb%2Cfl_progressive%2Cq_auto:eco%2Cw_1200/MTc1ODkxNDc0MzU1ODU3Mjcz/a-letter-from-a-dying-man-ii-timothy-1.jpg")
Book.create(name: "Titus", testament: "New Testament", author: "Paul", group: "Paul's Letters", description: "A letter of guidance from Paul to Titus to address challenges facing his leadership of the churches on the island of Crete.", image_url: "https://bcnewton.files.wordpress.com/2016/03/titus-bg.jpg?w=640")
Book.create(name: "Philemon", testament: "New Testament", author: "Paul", group: "Paul's Letters", description: "Consisting of only one chapter, the book is a short but profound letter from Paul to Philemon requesting forgiveness for a runaway slave named Onesimus.", image_url: "https://lh3.googleusercontent.com/proxy/07qeJcqkBobH15cyv857MRhSZ_CTO_vP0IHJ2LZfSN_DWP1i5_-Az7dYOwgeMWjZ19LXoeNibPiWXxVYBwLO58klYJq22FO5rhTRZD4XZMt12O0nr6Ig63Q")
Book.create(name: "Hebrews", testament: "New Testament", author: "Unknown", group: "General Letters", description: "A letter urging Jewish believers not to return to their former traditions, summarising key Biblical characters and events to emphasise the hope of salvation through Jesus.", image_url: "https://woodfieldsbaptist.org/wp-content/uploads/2018/09/hebrews-1.jpg")
Book.create(name: "James", testament: "New Testament", author: "James", group: "General Letters", description: "A hard-hitting letter from James, encouraging believers to have a genuine faith with an emphasis on results.", image_url: "https://dq5pwpg1q8ru0.cloudfront.net/2020/10/17/15/11/06/8914b498-15df-4532-a54c-2bb98e4fcb54/book-of-james1.jpg")
Book.create(name: "1 Peter", testament: "New Testament", author: "Peter", group: "General Letters", description: "The focus of Peter's first letter is persecution, sharing inspiring words of comfort for Christians living as an oppressed minority in the Roman Empire.", image_url: "https://www.gotquestions.org/img/OG/Book-of-1-Peter.jpg")
Book.create(name: "2 Peter", testament: "New Testament", author: "Peter", group: "General Letters", description: "Peter's second letter warns against false teachers and reaffirms important spiritual truths.", image_url: "https://i.ytimg.com/vi/CYexo4tre_8/mqdefault.jpg")
Book.create(name: "1 John", testament: "New Testament", author: "John", group: "General Letters", description: "Written to oppose heretical doctrine, the first letter from John echoes the Gospel, encouraging Christians to love one another and keep Jesus' commands.", image_url: "https://www.gotquestions.org/img/OG/Book-of-1-John.jpg")
Book.create(name: "2 John", testament: "New Testament", author: "John", group: "General Letters", description: "A brief letter from John to the chosen lady, urging believers to love one another and to be on guard against false teachings.", image_url: "https://www.gotquestions.org/img/OG/Book-of-2-John.jpg")
Book.create(name: "3 John", testament: "New Testament", author: "John", group: "General Letters", description: "The shortest book in the Bible, 3 John commends Gaius and Demetruis for their faithful service.", image_url: "https://calvarysomersetwest.co.za/images/teachings/3John.jpg")
Book.create(name: "Jude", testament: "New Testament", author: "Jude", group: "General Letters", description: "A letter from Jude to address false teachings and urge Christians to defend the truth of the Good News.", image_url: "https://cdn.neverthirsty.org/wp-content/uploads/2020/02/jude-header.jpg")
Book.create(name: "Revelation", testament: "New Testament", author: "John", group: "Apocalyptic", description: "Written during John's imprisonment on the island of Patmos, Revelation is an apocalyptic book that contains prophetic visions of the Spiritual Realms and Jesus' return to Earth.", image_url: "https://perrystone.org/wp-content/uploads/2018/06/book-of-revelation-apocalypse-code.jpg")